const request = require('request')
let run_id = context.source.run_id
let url = "http://thermoc:8081/api/shutter/status"
let timing = 1000

// run http://localhost:8081/api/shutter/status to get the status
function tick() {
  // avoid running duplicate timers
  if( run_id != context.source.run_id ) {
    console.log("removing timer handler " + context.source.run_id + " " + run_id)
    return
  }  

  // find source
  let sources = context.projectManager.sources()
  let source = sources[context.source.id]

  request(url, function(err, resp, body) {
    let response = {}
    try { response = JSON.parse(body) } catch(err) {}

    if ( response.status == "ok" ) {
      // update shutter data
      shutters = source.data
      shutters.forEach( (target) => {
        response.shutter.forEach( (source) => {
          if( target.id == source.id ) {
            // update status of the shutter
            target.status = source.status
          }
        } )
      } )

      // send out the update to all windows
      context.projectManager.send('source.changed', source)

      // run next interval
      setTimeout( tick , timing )
    }

  })
}

// initiate the script
tick()

