/*
context = {
arg, projectManager
}
*/
//
// Valve Toggle Button
// if the valve is open then send close valve
// else if the vale is closed then send open valve

const request = require('request')

// element
let element = context.arg.element
// sources
let source = context.arg.source
// see the text
let text = element.parameter.text
// see the id
let rowId = element.parameter.rowId
// see the type
let type = element.parameter.type

// api url
const open_url = 'http://thermoc:8081/api/'+type+'/open/'+rowId
const close_url = 'http://thermoc:8081/api/'+type+'/close/'+rowId

function updateText(text) {
  let row = source.data.find(o => o.id == element.parameter.rowId)
  row[element.parameter.columnName] = text
  context.projectManager.send('source.changed', source)
}

if ( text == "open" ) {
  updateText("closing")
  // close shutter
  request(close_url)
}

else if ( text == "closed" ) {
  updateText("opening")
  // open shutter
  request(open_url)
}

