action.script:
var c = this

c.jQuery.ajax({
	url: 'http://www.tok.ipp.cas.cz/it/computer/post/view/'+c.property.id+'?json=1',
	async: true,
	dataType: "jsonp" ,
	success: function(res) {
  	// logged in users as description
        c.property.description = res.users.join(", ")

  	// last updated
  	var now = new Date();
  	var updated = new Date(res.updated);
  	var diff = Math.round((((now - updated) % 86400000) % 3600000) / 60000); // minutes
  	if ( diff < 20 ) {
    	c.property.title_bg_color = "green"
    }
  	else {
    	c.property.title_bg_color = "red"
    }
    // send out property changed
    c.event.emit(c.NodeEvents.PROPERTY_CHANGED, c.property);
	}
});

